mapper.select:

select

  distinct

'true' as QUERYID,

from live




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}


select 

from live
where live_uid = #{live_uid,jdbcType=BIGINT}
  and sid = #{sid,jdbcType=BIGINT}
  and ssid = #{ssid,jdbcType=BIGINT}

mapper.delete:

delete from live
where live_uid = #{live_uid,jdbcType=BIGINT}
  and sid = #{sid,jdbcType=BIGINT}
  and ssid = #{ssid,jdbcType=BIGINT}
